import React, { Component } from 'react';
import Box from '@mui/material/Box';
import './Login.css'
import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';
import axios from 'axios';


export default class Login extends Component {
    
  state = {
    username: '',
    password: '',
    output: '',
  }

  render() {
    return (
      <div class="form"> 
      <p>LOGIN </p>
      <div class="inputDiv">
        <TextField required id="outlined-basic" name="username" label="Username" onChange={this.handleChange} variant="outlined" />
        </div>
        <div class="inputDiv">
        <TextField id="outlined-basic" name="password" label="Password" onChange={this.handleChange} variant="outlined" />    
        </div>
        <div class="btnDiv">
        <Button variant="contained" 
           onClick={this.handleClick}>Submit</Button>
        </div>
        <p> </p>
     </div>
    );

  }

   handleChange = event => {
    if (event.target.name == "username"){
      this.setState({ 
        username: event.target.value 
      });
    }else if (event.target.name == "password"){
      this.setState({ 
        password: event.target.value 
      });
    }
  }


  handleClick = async () => {
    
    console.log(this.state);
    if(this.state.username.length == 0 || this.state.password.length == 0 ){
      console.log("sssssssss")
    }else {

    try {
      const {res} = await axios.post(`http://api.cedalco.com/Users/authenticate` , {
        username : this.state.username,
        password : this.state.password
      });
      console.log(res);
    } catch(error) {
      console.log(error.response.data);
    }

  }

  }
    
}
