import React, { Component } from 'react';
import Box from '@mui/material/Box';
import './Login.css'
import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';
import axios from 'axios';


export default class Login extends Component {
    
  state = {
    data: { username: "", password: "" },
    name: '',
    password: ''
  }

  render() {
    return (
      <div class="form"> 
      <p>LOGIN </p>
      <div class="inputDiv">
        <TextField id="outlined-basic" name="name" label="Username" onChange={this.handleChange} variant="outlined" />
        </div>
        <div class="inputDiv">
        <TextField id="outlined-basic" name="password" label="Password" variant="outlined" />    
        </div>
        <div class="btnDiv">
        <Button variant="contained" 
           onClick={this.handleClick}>Submit</Button>
        </div>
     </div>
    );

  }

   handleChange = event => {
    this.setState({ name: event.target.value });
  }


  handleClick = async () => {
    
      const user = {
      name: this.state.name
    };

   const pass = {
      name: this.state.password
    };

    try{
      const {res} = await axios.post(`http://api.cedalco.com/Users/authenticate` , { user } , {pass});
      console.log(res);
    }catch(error){
      console.log(error.response.data, "error");
    }

  }
    
}
